package com.jhm69.quizapp_hometask;import static android.content.Intent.FLAG_ACTIVITY_CLEAR_TASK;import android.content.Context;import android.content.Intent;import android.content.SharedPreferences;import android.os.Bundle;import androidx.appcompat.app.AppCompatActivity;import androidx.appcompat.app.AppCompatDelegate;import java.util.Timer;import java.util.TimerTask;public class Splash extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);                setContentView(R.layout.activity_splash);        new Timer().schedule(new TimerTask() {            @Override            public void run() {                    startActivity(new Intent(Splash.this, MainActivity.class).setFlags(FLAG_ACTIVITY_CLEAR_TASK));                    finish();            }        }, 1600);    }}