package com.jhm69.quizapp_hometask.db;import android.arch.persistence.room.Database;import android.arch.persistence.room.Room;import android.arch.persistence.room.RoomDatabase;import android.content.Context;import com.jhm69.quizapp_hometask.dao.DaoAccess;import com.jhm69.quizapp_hometask.model.Quiz;@Database(entities = {Quiz.class}, version = 1, exportSchema = false)public abstract class QuizResultDatabase extends RoomDatabase {    private static QuizResultDatabase instance;    public static synchronized QuizResultDatabase getInstance(Context context) {        if (instance == null) {            instance = Room.databaseBuilder(context.getApplicationContext(),                    QuizResultDatabase.class, "quiz_app")                    .allowMainThreadQueries()                    .fallbackToDestructiveMigration()                    .build();        }        return instance;    }    public abstract DaoAccess daoAccess();}