package com.jhm69.quizapp_hometask.retrofit;import com.jhm69.quizapp_hometask.model.Question;import java.util.List;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.Query;public interface QuestionService {    @GET("questions/")    Call<List<Question>> getQuestions(@Query("categories") String categories, @Query("difficulty") String difficulty);}