package com.jhm69.quizapp_hometask.utils;import android.arch.persistence.room.TypeConverter;import com.google.gson.Gson;import com.google.gson.reflect.TypeToken;import com.jhm69.quizapp_hometask.model.Question;import java.lang.reflect.Type;import java.util.List;public class DataTypeConverterQuestion {    private static final Gson gson = new Gson();    @TypeConverter    public static List<Question> stringToList(String data) {        if (data == null) {            return null;        }        Type listType = new TypeToken<List<Question>>() {        }.getType();        return gson.fromJson(data, listType);    }    @TypeConverter    public static String ListToString(List<Question> questions) {        return gson.toJson(questions);    }}