package com.jhm69.quizapp_hometask.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import com.jhm69.quizapp_hometask.model.Quiz;import com.jhm69.quizapp_hometask.repository.QuizResultRepository;import java.util.List;public class BattleViewModel extends AndroidViewModel {    public final LiveData<List<Quiz>> battles;    final QuizResultRepository repository;    public BattleViewModel(@NonNull Application application) {        super(application);        repository = new QuizResultRepository(application);        battles = repository.getAllNotes();    }    public void insert(Quiz result) {        repository.insertBattle(result);    }    public LiveData<Quiz> getBattle(String id) {        return repository.getBattleOffline(id);    }    public void delete(String result) {        repository.deleteBattle(result);    }}